    cell.svetac.text = (self.spisakSvetacaZaJun)[indexPath.row];
        // Configure

    cell.noviDatum.text = [NSString stringWithFormat:@"%ld.",indexPath.row +1];
    cell.danUNedelji.text = self.title;
    return cell;

